import { IJWK } from "@extrimian/kms-core";
import ModenaSdkConfig from "../ModenaSdkConfig";
import Multihash from "../Multihash";

export class UpdateCommitmentUtils {

    static getUpdateCommitmentHash(updateKeys: IJWK) {
        const hashAlgorithmInMultihashCode = ModenaSdkConfig.hashAlgorithmInMultihashCode;

        return Multihash.canonicalizeThenDoubleHashThenEncode(updateKeys, hashAlgorithmInMultihashCode);
    }
}